import React from 'react';
import NewTweet from './components/NewTweet';
import Tweets from './components/Tweets';
import * as api from './api/apiService';



const handleData = async (formData) => {
  const { tweet } = formData;
  let id = await api.getAllTweets();
  id = id.map(id => id.id)
  let newId = id.length + 1;
  let tweets = {
    id : newId,
    value: tweet
  }

  api.insertTweet(tweets)
}

export default function App() {


  return (
    <div>
      <h1 className="center" style={styles.title}>React Twitter</h1>
      <NewTweet onSave={handleData} />
      <Tweets />
    </div>
  );
}
const styles = {
  title: {
    fontWeight: 'bold',
    color: '#55cf4e'
  }
}
