import React, { useEffect, useState } from 'react'
import * as api from '../api/apiService';





export default function Tweets() {

    const [allTweets, setALLTweets] = useState([]);

    useEffect(() => {
        const getTweets = async () => {
            const tweets = await api.getAllTweets();
            setALLTweets(tweets)
        }
        getTweets()
    }, []);



    return (
        <div className="container">
            <div className="collection" style={{ marginTop: '70px' }}>

                {allTweets.map(item => {
                    return (
                        <a href="#!" className="collection-item" key={item.id} style={{ margin: '10px', color: 'black' }}>
                            {item.value}
                            <button style={styles.btn } className="material-icons right">delete</button>
                        </a>
                    );
                })}
            </div>
        </div>
    )
}

const styles = {
    btn: {
        background: 'none',
        color: '#66bb6a',
        backgroundColor: 'transparent',
        border: 'none',
        cursor: 'pointer',

        "&:hover": {
            color: 'red'
        }
    },
    
}
