import React from 'react'


export default function NewTweet({ onSave }) {

    const handleSubmit = (e) => {
        e.preventDefault();
        //console.log(e.target[0].value)
        let FormData = {
            tweet: `${e.target[0].value}`
        }
        onSave(FormData)
    }


    function getLenght() {
        console.log( document.getElementById('#texarea'))
    }
    getLenght()








    return (
        <form onSubmit={handleSubmit}>
            <div className='container'>
                <div className="input-field col s12 center">
                    <textarea rows='12' id="texarea" cols='12' maxLength='280' style={styles.textarea} placeholder='Type anything' data-length="280"></textarea>
                </div>
                <label>{} caracteres</label>
                <button style={styles.btn} className="waves-effect green lighten-1 btn right" href=' '>Tweet</button>
            </div>
        </form>
    )
}
const styles = {
    textarea: {
        resize: 'none',
        width: '100%',
        height: '150px',
        borderRadius: '9px',
        borderColor: '#66bb6a '
    },
    btn: {
        background: 'none',
        backgroundColor: 'transparent',
        border: 'none',
        cursor: 'pointer'
    }
}
