import React, { useEffect, useState } from 'react'
import * as api from '../api/apiService';





export default function Tweets() {

    const [allTweets, setALLTweets] = useState([]);

    useEffect(() => {
        const getTweets = async () => {
            const tweets = await api.getAllTweets();
            setALLTweets(tweets)
        }
        getTweets()
    }, []);

    const handleTweet = () => {
        var buttons = document.querySelectorAll('button[id=buttons]')
        buttons.forEach(button => {
            button.addEventListener('click', (e) => {
                console.log(e)
            })
        })
    }


    return (
        <div className="container">
            <div className="collection" style={{ marginTop: '70px' }}>
                <form onSubmit={handleTweet}>
                    {allTweets.map(item => {
                        return (
                            <a href="#!" className="collection-item" id={item.id} key={item.id} style={{ margin: '10px', color: 'black' }}>
                                {item.value}
                                <button style={styles.btn} id="buttons" className="material-icons right">delete</button>
                            </a>
                        );
                    })}
                </form>
            </div>
        </div>
    )
}

const styles = {
    btn: {
        background: 'none',
        color: '#66bb6a',
        backgroundColor: 'transparent',
        border: 'none',
        cursor: 'pointer',
    },

}
