import React from 'react'


export default function NewTweet() {
    return (
        <div className='container'>
            <div className="input-field col s12 center">
                <textarea rows='12' cols='12' maxLength='280' style={styles.textarea} placeholder='Type anything' data-length="120"></textarea>
            </div>
            <button  style={styles.btn} className="waves-effect green lighten-1 btn right" href=' '>Tweet</button>
        </div>
    )
}
const styles = {
    textarea: {
        resize: 'none',
        width: '100%',
        height: '150px',
        borderRadius: '9px',
        borderColor: '#66bb6a '
    },
    btn: {
        background: 'none',
        color: '#66bb6a',
        backgroundColor: 'transparent',
        border: 'none',
        cursor: 'pointer'
}
}
