import React, { useEffect, useState } from 'react'
import * as api from '../api/apiService';





export default function Tweets() {

    const [allTweets, setALLTweets] = useState([]);

    useEffect(() => {
        const getTweets = async () => {
            const tweets = await api.getAllTweets();
            setALLTweets(tweets)
        }
        getTweets()
    }, []);



    return (
        <div className="container">
            <div className="collection" style={{ marginTop: '70px' }}>

                {allTweets.map(item => {
                    return (
                        <a href="#!" className="collection-item" style={{ margin: '10px' }}>
                            {item.value}
                            <span style={{ color: '#66bb6a' }} className="material-icons right">delete</span>
                        </a>
                    );
                })}
            </div>
        </div>
    )
}
