import React from 'react'

export default function NewTweet() {
    return (
        <div className='container'>
            <div className="input-field col s12 center">
                <textarea rows='12' cols='12' maxLength='280' style={styles.textarea} placeholder='Type anything' data-length="120"></textarea>
            </div>
            <a class="waves-effect waves-light btn right">Tweet</a>
        </div>
    )
}
const styles = {
    textarea: {
        resize: 'none',
        width: '100%',
        height: '150px',
        border: 'bold'
    }
}
