import React from 'react'
import styles from './newtweet.module.css';

export default function NewTweet({ onSave }) {

    const handleSubmit = (e) => {
        e.preventDefault();
        //console.log(e.target[0].value)
        let FormData = {
            tweet: `${e.target[0].value}`
        }
        onSave(FormData)
    }

  

  


    return (
        <form onSubmit={handleSubmit}>
            <div className='container'>
                <div className="input-field col s12 center">
                    <textarea rows='12' id="text-area" cols='12' maxLength='280' style={styles.textarea} placeholder='Type anything' data-length="280"></textarea>
                </div>
                <label>caracteres</label>
                <button style={styles.btn} className="waves-effect green lighten-1 btn right" href=' '>Tweet</button>
            </div>
        </form>
    )
}
